import React, { Component } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import './MainPage.css';
import Register from "./Register";
import Login from "./Login";

class MainPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            from: '',
            to: '',
            departureDate: '',
            returnDate: '',
            passengers: '1',
        };
    }

    handleInputChange = (event) => {
        this.setState({ passengers: event.target.value });
    };


    render() {
        return (
            <div className="master">
                <div className="navBar">
                    <div className="mainContainer">
                        <div className="firstNav">
                            <nav>
                                <ul>
                                    <li>
                                        <Link to="/">Contact</Link>
                                    </li>
                                    <li>
                                        <Link to="/">About us</Link>
                                    </li>
                                </ul>
                            </nav>
                        </div>
                        <div className="middleNav">
                            <div style={{width: '210px'}}>
                                <p>Меѓународна - Меѓуградска <b>Автобуска станица - Скопје</b></p>
                            </div>
                            <div className='img'>

                            </div>
                            <div style={{width: '150px'}}>
                                <p>International - Intercity <b>Bus Station - Skopje</b></p>
                            </div>
                        </div>
                        <div className="button-container">
                            <button className="signin-button"><Link to="/login">Најави се</Link></button>
                            <button className="signup-button"><Link to="/register">Регистрирај се</Link></button>
                        </div>
                    </div>
                </div>
                <div className="mainDiv">
                    <div className="searchRoute">
                        <form>
                            <input
                                type="text"
                                name="from"
                                placeholder="From"
                                value={this.state.from}
                                onChange={this.handleChange}
                                required
                            />
                            <input
                                type="text"
                                name="to"
                                placeholder="To"
                                value={this.state.from}
                                onChange={this.handleChange}
                                required
                            />
                            <input
                                type="date"
                                name="departureDate"
                                placeholder="Departure Date"
                                value={this.state.departureDate}
                                onChange={this.handleChange}
                                required
                            />
                            <input
                                type="date"
                                name="returnDate"
                                placeholder="Return Date"
                                value={this.state.returnDate}
                                onChange={this.handleChange}
                                required
                            />
                            <input
                                type="number"
                                placeholder="Passengers"
                                value={this.state.passengers}
                                min="1"
                                max="30"
                                step="1"
                                onChange={this.handleInputChange}
                            />
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

export default MainPage;
